import React,{ useState} from 'react'
import { useDispatch, useSelector } from 'react-redux'
import { useNavigate } from 'react-router-dom'
import { askQuestion } from '../../actions/question.js'

import './AskQuestion.css'

const AskQuestion = () => {

    const [questionTitle, setQuestionTitle] = useState('');
    const [questionBody, setQuestionBody] = useState('');
    const [questionTags, setQuestionTags] = useState('');

    const dispatch = useDispatch()
    const User = useSelector((state) => (state.currentUserReducer))
    const navigate = useNavigate()

    const handleSubmit = (e) => {
        e.preventDefault()
        dispatch(askQuestion( { questionTitle, questionBody, questionTags, userPosted: User.result.name, userId: User?.result?._id}, navigate) )
    }

    const handleEnter = (e) =>{
        if(e.key === 'Enter'){
            setQuestionBody(questionBody + "\n")
        }
    }
    return (
    <>
        <div className='ask-question'>
            <div className='ask-ques-container'>
                <h1>Ask a public Question</h1>
                <h1>{questionBody}</h1>
                {/* ==================================================== FORM ====================================== */}
                <form onSubmit={handleSubmit}>
                    <div className="ask-form-container">

                        {/* ============================================ Title ====================================== */}
                        <label htmlFor="ask-ques-title">
                            <h4>Title</h4>
                            <p>Be specific and imagine you're asking a question to another person</p>
                            <input type='text' id='ask-ques-title' onChange={(e) => {setQuestionTitle(e.target.value)}} placeholder='e.g. Is there an R function for finding the index of an element in a vector?'/>
                        </label>

                        {/* ============================================ Body ====================================== */}
                        <label htmlFor="ask-ques-body">
                            <h4>Body</h4>
                            <p>Include all the information someone would need to answer your question</p>
                            <textarea name="" id="ask-ques-body"  cols="30" rows="10"  onChange={(e) =>{setQuestionBody(e.target.value)}} onKeyPress={handleEnter}></textarea>
                        </label>

                         {/* ============================================ Tags ====================================== */}
                        <label htmlFor="ask-ques-tags">
                            <h4>Tags</h4>
                            <p>Add up to 5 tags to descriobe what your question is about</p>
                            <input type='text' id='ask-ques-tags' onChange={(e) =>{setQuestionTags(e.target.value.split(" "))}} placeholder='e.g. (xml typescript wordpress)'/>
                        </label>
                    </div>
                     {/* ============================================ Submit ====================================== */}
                    <input type='submit' value='Review your question' className='review-btn'/>
                </form>
            </div>

        </div>
    </>

    )
}

export default AskQuestion;
