export const questionsReducer = (state = {data:null}, action) => {
  switch(action.type){
      case "POST_QUESTION":
          return { ...state };
      case "POST_ANSWER":
          return { ...state };
      case "FETCH_ALL_QUESTIONS":
          return { ...state, data: action.payload};
      case 'ASKED_QUESTIONS':
            return { ...state, Asked: action.payload}
      default:
          return state
  };
};
